start_date &lt;- ymd("1981-09-01")

number_of_nc_files &lt;- interval(start_date, last_valid_date) %/% days(1) + 1
number_of_tempo_files &lt;-
  interval(last_valid_date, last_tempo_date) %/% days(1)
